var app=new Vue({el:"#exercise",data:{value:0,threshold:36,timer:5e3},computed:{result:function(){return console.log("Result updated to "+this.value),this.value<1?"":this.value<2?"Just "+this.value+"  jump, uh?":this.value<36?"Just "+this.value+" jumps in a row, uh?":36==this.value?"Wow! "+this.value+" jumps in a row!":"Wow! More than "+this.value+" jumps in a row!"}},watch:{result:function(){var e=this;e.value==e.threshold&&setTimeout(function(){console.log("Result resetted."),e.value=0},e.timer)}}});