var app=new Vue({el:"#app",data:{counter:0,counter2:0,counter3:0,x:0,y:0},methods:{increase:function(){this.counter++},increase2:function(n){this.counter2+=n},increase3:function(n,t){this.counter3+=n},updateCoordinates:function(n){this.x=n.clientX,this.y=n.clientY},notUpdateCoordinates:function(n){n.stopPropagation()},logMe:function(n){console.log("You just pressed ENTER or SPACE.")}}});