function hexToRgb(t){var s=/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(t);return s=[parseInt(s[1],16),parseInt(s[2],16),parseInt(s[3],16)]}var exercise=new Vue({el:"#exercise",data:{cHSLuv1:[0,0,0],cRGB1:[0,0,0],cHEX1:"#000000"},methods:{debug:function(){console.log("HSLuv: ["+this.cHSLuv1[0]+", "+this.cHSLuv1[1]+", "+this.cHSLuv1[2]+"]."),console.log("RGB: ["+this.cRGB1[0]+", "+this.cRGB1[1]+", "+this.cRGB1[2]+"]."),console.log("HEX: ["+this.cHEX1+"].")},translateHSL:function(){var t=window.hsluv.hsluvToRgb(this.cHSLuv1);this.cRGB1=[Math.round(255*t[0]),Math.round(255*t[1]),Math.round(255*t[2])],this.cHEX1=window.hsluv.hsluvToHex(this.cHSLuv1),this.debug()},translateRGB:function(){var t=window.hsluv.rgbToHsluv([this.cRGB1[0]/255,this.cRGB1[1]/255,this.cRGB1[2]/255]);this.cHSLuv1=[Math.round(t[0]),Math.round(t[1]),Math.round(t[2])],this.cHEX1=window.hsluv.hsluvToHex(this.cHSLuv1),this.debug()},translateHEX:function(){this.cRGB1=hexToRgb(this.cHEX1);var t=window.hsluv.rgbToHsluv([this.cRGB1[0]/255,this.cRGB1[1]/255,this.cRGB1[2]/255]);this.cHSLuv1=[Math.round(t[0]),Math.round(t[1]),Math.round(t[2])],this.debug()}}});