function hexToRgb(t){var s=/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(t);return s=[parseInt(s[1],16),parseInt(s[2],16),parseInt(s[3],16)]}var exercise=new Vue({el:"#exercise",data:{c1:{HSL:[40,80,80],RGB:[246,186,145],HEX:"#f6ba91",A:.8}},methods:{debug:function(t){console.log("HSLuv: ["+this[t].HSL[0]+", "+this[t].HSL[1]+", "+this[t].HSL[2]+", "+this[t].A+"]."),console.log("RGB: ["+this[t].RGB[0]+", "+this[t].RGB[1]+", "+this[t].RGB[2]+", "+this[t].A+"]."),console.log("HEX: ["+this[t].HEX+", "+this[t].A+"].")},translateHSL:function(t){var s=window.hsluv.hsluvToRgb(this[t].HSL);this[t].RGB=[Math.round(255*s[0]),Math.round(255*s[1]),Math.round(255*s[2])],this[t].HEX=window.hsluv.hsluvToHex(this[t].HSL),this.debug(t)},translateRGB:function(t){var s=window.hsluv.rgbToHsluv([this[t].RGB[0]/255,this[t].RGB[1]/255,this[t].RGB[2]/255]);this[t].HSL=[Math.round(s[0]),Math.round(s[1]),Math.round(s[2])],this[t].HEX=window.hsluv.hsluvToHex(this[t].HSL),this.debug(t)},translateHEX:function(t){this[t].RGB=hexToRgb(this[t].HEX);var s=window.hsluv.rgbToHsluv([this[t].RGB[0]/255,this[t].RGB[1]/255,this[t].RGB[2]/255]);this[t].HSL=[Math.round(s[0]),Math.round(s[1]),Math.round(s[2])],this.debug(t)},paint:function(t,s){return"alpha"==s?{"background-color":this[t].HEX}:{"background-color":"rgba("+this[t].RGB[0]+","+this[t].RGB[1]+","+this[t].RGB[2]+","+this[t].A+")"}}}});