function hexToRgb(t){var s=/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(t);return s=[parseInt(s[1],16),parseInt(s[2],16),parseInt(s[3],16)]}var exercise=new Vue({el:"#exercise",data:{c1:{HSL:[0,0,0],RGB:[0,0,0],HEX:"#000000"}},methods:{debug:function(t){console.log("HSLuv: ["+this[t].HSL[0]+", "+this[t].HSL[1]+", "+this[t].HSL[2]+"]."),console.log("RGB: ["+this[t].RGB[0]+", "+this[t].RGB[1]+", "+this[t].RGB[2]+"]."),console.log("HEX: ["+this[t].HEX+"].")},translateHSL:function(t){var s=window.hsluv.hsluvToRgb(this[t].HSL);this[t].RGB=[Math.round(255*s[0]),Math.round(255*s[1]),Math.round(255*s[2])],this[t].HEX=window.hsluv.hsluvToHex(this[t].HSL),this.debug(t)},translateRGB:function(t){var s=window.hsluv.rgbToHsluv([this[t].RGB[0]/255,this[t].RGB[1]/255,this[t].RGB[2]/255]);this[t].HSL=[Math.round(s[0]),Math.round(s[1]),Math.round(s[2])],this[t].HEX=window.hsluv.hsluvToHex(this[t].HSL),this.debug(t)},translateHEX:function(t){this[t].RGB=hexToRgb(this[t].HEX);var s=window.hsluv.rgbToHsluv([this[t].RGB[0]/255,this[t].RGB[1]/255,this[t].RGB[2]/255]);this[t].HSL=[Math.round(s[0]),Math.round(s[1]),Math.round(s[2])],this.debug(t)},test:function(t){this.debug(t)}}});