var app=new Vue({el:"#project",data:{playerHealth:100,monsterHealth:100,gameOn:!1,turns:[]},methods:{startNewGame:function(){console.log("I started a new game started."),this.playerHealth=100,this.monsterHealth=100,this.gameOn=!0,this.turns=[]},attack:function(){console.log("Attack."),this.playerAttack(2,10),this.evaluateResult()||(this.monsterAttack(4,12),this.evaluateResult())},specialAttack:function(){console.log("Special attack."),this.playerAttack(10,20),this.evaluateResult()||(this.monsterAttack(8,16),this.evaluateResult())},heal:function(){console.log("Heal."),this.playerHealth<=100?(this.playerHeal(4,12),this.playerHealth>=100&&(this.playerHealth=100)):this.playerHealth=100,this.monsterAttack(4,12),this.evaluateResult()},surrender:function(){console.log("Surrend."),this.gameOn=!1},playerHeal:function(t,e){var a=this.rollDices(t,e);this.playerHealth+=a,this.turns.unshift({isPlayer:!0,text:"Player heals itself for "+a+"."}),console.log("Player: "+a+".")},playerAttack:function(t,e){var a=this.rollDices(t,e);this.monsterHealth-=a,this.turns.unshift({isPlayer:!0,text:"Player hits monster for "+a+"."}),console.log("Player: "+a+".")},monsterAttack:function(t,e){var a=this.rollDices(t,e);this.playerHealth-=a,this.turns.unshift({isPlayer:!1,text:"Monster hits player for "+a+"."}),console.log("Monster: "+a+".")},rollDices:function(t,e){return Math.max(Math.floor(Math.random()*e)+1,t)},evaluateResult:function(){return this.monsterHealth<=0?(confirm("You won! Wanna play again?")?this.startNewGame():this.gameOn=!1,!0):this.playerHealth<=0&&(confirm("You lost! Wanna play again?")?this.startNewGame():this.gameOn=!1,!0)}}});